var crypto = require('crypto');
var Benchmark = require('benchmark');
var generatePrimes = require('./generatePrime');
var total = 1000;
var out = new Array(total);
var i = -1;
var ss =  require("simple-statistics");
while (++i < total) {
  out[i] = generatePrimes(64, crypto);
  console.log(i);
}
var time = out.map(function (i) {
  return i.time;
});
var tests = out.map(function (i) {
  return i.tests;
});
var runs = out.map(function (i) {
  return i.runs;
});
var stat = {
  time: {
    mean: ss.mean(time),
    median: ss.median(time),
    sum: ss.sum(time),
    max: ss.max(time),
    min: ss.min(time)
  },
  tests: {
    mean: ss.mean(tests),
    median: ss.median(tests),
    sum: ss.sum(tests),
    max: ss.max(tests),
    min: ss.min(tests)
  },
  runs: {
    mean: ss.mean(runs),
    median: ss.median(runs),
    sum: ss.sum(runs),
    max: ss.max(runs),
    min: ss.min(runs)
  }
};
console.log(JSON.stringify(stat, false, 4));